script:
frappe.ui.form.on("HD Ticket", {
    onload(frm) {
        // Run for new tickets or in Helpdesk UI
        if (frm.is_new() || window.location.pathname.includes("/helpdesk/tickets")) {
            // Fetch the site name from the server
            frappe.call({
                method: "alphax_support.support.utils.get_current_site",
                callback: function(r) {
                    if (r.message) {
                        console.log("Fetched Site Name:", r.message);
                        frm.set_value("custom_site_name", r.message);
                    } else {
                        console.log("Could not fetch site name, using fallback.");
                        frm.set_value("custom_site_name", "Not specified");
                    }
                },
                error: function(err) {
                    console.error("Error fetching site name:", err);
                    frm.set_value("custom_site_name", "Not specified");
                }
            });

            // Ensure ticket_type has a default value to trigger dependency logic
            if (!frm.doc.ticket_type) {
                frm.set_value("ticket_type", "Unspecified");
            }
        }
    },
    refresh(frm) {
        // Also run on refresh for standard forms
        if (frm.is_new()) {
            frappe.call({
                method: "alphax_support.support.utils.get_current_site",
                callback: function(r) {
                    if (r.message && !frm.doc.custom_site_name) { // Only set if not already set
                        console.log("Fetched Site Name (refresh):", r.message);
                        frm.set_value("custom_site_name", r.message);
                    }
                }
            });
        }
    },
});
